args:
.btn-icon-edit {
  width: 28px;
  height: 28px;
  border: none;
  background: none;
  border-radius: 4px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 14px;
  color: #94a3b8;
  transition: all 0.2s;
}

.btn-icon-edit:hover {
  background-color: #e2e8f0;
  color: #3b82f6;
}
